#' Display  add Bayer products,show Bayer products save it into local file
#'
#' @param input
#' @param output
#' @param session
#' @description This code have a  R shiny packages and one function for add products and display the product price,expiry date of product
#' @author Sukumar Mahabaleshwar Puranik
#'
#' @return Return added data  to local file
#'
#' @examples
server <- function(input, output, session) {
  data <- reactiveValues(products = data.frame())

  observeEvent(input$submit_btn, {
    # Create a data frame with the input values
    new_product <- data.frame(
      ProductName = input$productName,
      ProductPrice = input$productPrice,
      ExpDate = input$expDate
    )

    # Append the new row to the existing data frame
    data$products <- rbind(data$products, new_product)

    # Save the data frame to an Excel file
    write_xlsx(data$products, "/Users/efazl/Documents/ProductBayer.xlsx")

    # Render the data frames as DataTables
    output$myTable <- DT::renderDataTable({
      Data_gfg <- read_excel("/Users/efazl/Documents/ProductBayer.xlsx")
      Data_gfg
    })


    # Validate product name
    if (is.null(input$productName) || input$productName == "") {
      showNotification("Please enter a product name.", type = "warning")
      return()
    }

    # Validate product price
    if (is.null(input$productPrice) || input$productPrice == "") {
      showNotification("Please enter a valid product price.", type = "warning")
      return()
    }

  })
}
shinyApp(ui, server)
